import React from 'react'

export const SegundoComponente = () => {
  let nombre = "Pablo Calvente";
    const cambiarNombre = (e) => {
        nombre = "Calvente, Pablo";
    }
    return (
    <div>
        <h3>Componente: useState</h3>
        <p>Nombre: {nombre}</p>
        <button onclick={cambiarNombre}>Cambiar Nombre</button>
    </div>
  )
}
